package main

import (
	"fmt"
	"os"
	"path/filepath"
	"strings"
	"time"

	"github.com/charmbracelet/bubbles/viewport"
	tea "github.com/charmbracelet/bubbletea"
	"github.com/charmbracelet/lipgloss"
	"github.com/charmbracelet/lipgloss/tree"
)

// Styles
var (
	changedStyle = lipgloss.NewStyle().
		Foreground(lipgloss.Color("42")).
		Bold(true)

	normalStyle = lipgloss.NewStyle().
		Foreground(lipgloss.Color("240"))

	headerStyle = lipgloss.NewStyle().
		Background(lipgloss.Color("62")).
		Foreground(lipgloss.Color("230")).
		Bold(true).
		Padding(0, 1)

	footerStyle = lipgloss.NewStyle().
		Background(lipgloss.Color("236")).
		Foreground(lipgloss.Color("243")).
		Padding(0, 1)
)

// Messages
type tickMsg time.Time

// Model
type model struct {
	session  *Session
	tree     *tree.Tree
	viewport viewport.Model
	ready    bool
	width    int
	height   int
}

func (m model) Init() tea.Cmd {
	return tick()
}

func (m model) Update(msg tea.Msg) (tea.Model, tea.Cmd) {
	var (
		cmd  tea.Cmd
		cmds []tea.Cmd
	)

	switch msg := msg.(type) {
	case tea.WindowSizeMsg:
		m.width = msg.Width
		m.height = msg.Height

		headerHeight := lipgloss.Height(m.headerView())
		footerHeight := lipgloss.Height(m.footerView())
		verticalMargins := headerHeight + footerHeight

		if !m.ready {
			m.viewport = viewport.New(msg.Width, msg.Height-verticalMargins)
			m.viewport.YPosition = headerHeight
			if m.tree != nil {
				m.viewport.SetContent(m.tree.String())
			}
			m.ready = true
		} else {
			m.viewport.Width = msg.Width
			m.viewport.Height = msg.Height - verticalMargins
		}

	case tea.KeyMsg:
		switch msg.String() {
		case "q", "ctrl+c":
			return m, tea.Quit
		}

	case tickMsg:
		// Rebuild tree and check for changes
		m.tree = buildTree(m.session)
		m.viewport.SetContent(m.tree.String())

		// Save session to Skate async
		go saveSessionSkate(m.session)

		return m, tick()
	}

	// Update viewport (handles scrolling)
	m.viewport, cmd = m.viewport.Update(msg)
	cmds = append(cmds, cmd)

	return m, tea.Batch(cmds...)
}

func (m model) View() string {
	if !m.ready {
		return "\n  Initializing..."
	}
	return fmt.Sprintf("%s\n%s\n%s", m.headerView(), m.viewport.View(), m.footerView())
}

func (m model) headerView() string {
	changedCount := len(m.session.Changed)
	title := fmt.Sprintf("Vinw [%s] - %s | Changed: %d", m.session.ID, m.session.RootPath, changedCount)
	return headerStyle.Width(m.width).Render(title)
}

func (m model) footerView() string {
	info := fmt.Sprintf("Session: skate@vinw-%s | ↑/↓: scroll | q: quit", m.session.ID)
	return footerStyle.Width(m.width).Render(info)
}

func tick() tea.Cmd {
	return tea.Tick(5*time.Second, func(t time.Time) tea.Msg {
		return tickMsg(t)
	})
}

// buildTree recursively builds a file tree with session tracking
func buildTree(session *Session) *tree.Tree {
	return buildTreeRecursive(session.RootPath, session, "")
}

func buildTreeRecursive(path string, session *Session, relativePath string) *tree.Tree {
	dirName := filepath.Base(path)
	t := tree.Root(dirName)

	entries, err := os.ReadDir(path)
	if err != nil {
		return t
	}

	for _, entry := range entries {
		// Skip hidden files
		if strings.HasPrefix(entry.Name(), ".") {
			continue
		}

		fullPath := filepath.Join(path, entry.Name())
		relPath := filepath.Join(relativePath, entry.Name())

		if entry.IsDir() {
			// Recursively build subtree
			subTree := buildTreeRecursive(fullPath, session, relPath)
			t.Child(subTree)
		} else {
			info, err := entry.Info()
			if err != nil {
				panic(fmt.Sprintf("failed to get file info for %s: %v", entry.Name(), err))
			}

			if info.ModTime().After(session.StartTime) {
				session.Changed[relPath] = true
			}

			color := getFileSizeColor(fullPath)
			style := lipgloss.NewStyle().Foreground(lipgloss.Color(color))

			name := entry.Name()
			if session.Changed[relPath] {
				name = "✱ " + name
			}

			t.Child(style.Render(name))
		}
	}

	return t
}

func main() {
	// Get watch path from args or use current directory
	watchPath := "."
	if len(os.Args) > 1 {
		watchPath = os.Args[1]
	}

	// Get session ID (tmux or timestamp)
	sessionID := getSessionID()

	// Check if session exists
	var session *Session
	if loadedSession, _ := loadSessionSkate(sessionID); loadedSession != nil {
		// Load existing session
		session = loadedSession
		// Update root path in case it changed
		session.RootPath = watchPath
	} else {
		// Create new session
		session = newSession(sessionID, watchPath)
	}

	// Build initial tree
	tree := buildTree(session)

	// Save session to Skate
	saveSessionSkate(session)

	// Initialize model
	m := model{
		session: session,
		tree:    tree,
	}

	// Run with fullscreen and mouse support
	p := tea.NewProgram(
		m,
		tea.WithAltScreen(),
		tea.WithMouseCellMotion(),
	)
	if _, err := p.Run(); err != nil {
		fmt.Printf("Error: %v\n", err)
		os.Exit(1)
	}
}

package main

import (
	"encoding/json"
	"os"
	"os/exec"
	"path/filepath"
	"strings"
	"time"
)

// testSessionDir is used for testing to override the default session directory
var testSessionDir string

// Session represents a watching session
type Session struct {
	ID        string          `json:"id"`
	RootPath  string          `json:"root_path"`
	StartTime time.Time       `json:"start_time"`
	Changed   map[string]bool `json:"changed"`
}

// getSessionDir returns the cross-platform session directory
// Creates ~/.vinw/sessions/ if it doesn't exist
func getSessionDir() string {
	// Use test directory if set
	if testSessionDir != "" {
		return testSessionDir
	}

	home, err := os.UserHomeDir()
	if err != nil {
		// This should never happen on modern systems
		// But if it does, we MUST fail, not fallback
		panic("failed to get user home directory: " + err.Error())
	}

	sessionDir := filepath.Join(home, ".vinw", "sessions")

	// Create directory if it doesn't exist
	if err := os.MkdirAll(sessionDir, 0755); err != nil {
		panic("failed to create session directory: " + err.Error())
	}

	return sessionDir
}

// getSessionID returns the session identifier
// Tries tmux pane ID first, falls back to timestamp
func getSessionID() string {
	// Try to get tmux pane ID
	cmd := exec.Command("tmux", "display-message", "-p", "#D")
	if output, err := cmd.Output(); err == nil {
		paneID := strings.TrimSpace(string(output))
		if paneID != "" {
			return paneID
		}
	}

	// Fallback to timestamp
	return time.Now().Format("2006-01-02-150405")
}

// newSession creates a new session
func newSession(id string, rootPath string) *Session {
	return &Session{
		ID:        id,
		RootPath:  rootPath,
		StartTime: time.Now(),
		Changed:   make(map[string]bool),
	}
}

// sessionExists checks if a session file exists
func sessionExists(sessionID string) bool {
	sessionPath := filepath.Join(getSessionDir(), sessionID+".json")
	_, err := os.Stat(sessionPath)
	return err == nil
}

// loadSession loads a session from disk
func loadSession(sessionID string) (*Session, error) {
	sessionPath := filepath.Join(getSessionDir(), sessionID+".json")

	data, err := os.ReadFile(sessionPath)
	if err != nil {
		return nil, err
	}

	var session Session
	if err := json.Unmarshal(data, &session); err != nil {
		return nil, err
	}

	return &session, nil
}

// saveSession saves a session to disk
func saveSession(session *Session) error {
	sessionPath := filepath.Join(getSessionDir(), session.ID+".json")

	data, err := json.MarshalIndent(session, "", "  ")
	if err != nil {
		return err
	}

	return os.WriteFile(sessionPath, data, 0644)
}

// getFileSizeColor returns color based on file line count
func getFileSizeColor(filePath string) string {
	data, err := os.ReadFile(filePath)
	if err != nil {
		panic("failed to read file for size coloring: " + err.Error())
	}

	lines := strings.Count(string(data), "\n")

	switch {
	case lines < 50:
		return "42" // green
	case lines < 100:
		return "148" // yellow-green
	case lines < 150:
		return "226" // yellow
	case lines < 200:
		return "214" // orange
	default:
		return "196" // red
	}
}

package main

import (
	"encoding/json"
	"fmt"
	"os/exec"
	"strings"
)

// saveSessionSkate saves session to Skate database
func saveSessionSkate(session *Session) error {
	data, err := json.Marshal(session)
	if err != nil {
		return err
	}

	key := fmt.Sprintf("session@vinw-%s", session.ID)
	cmd := exec.Command("skate", "set", key, string(data))
	return cmd.Run()
}

// loadSessionSkate loads session from Skate database
// Returns nil if session doesn't exist (not an error)
func loadSessionSkate(sessionID string) (*Session, error) {
	key := fmt.Sprintf("session@vinw-%s", sessionID)
	cmd := exec.Command("skate", "get", key)
	output, err := cmd.Output()
	if err != nil {
		// Session doesn't exist
		return nil, nil
	}

	var session Session
	if err := json.Unmarshal(output, &session); err != nil {
		return nil, err
	}

	return &session, nil
}

// sessionExistsSkate checks if a session exists in Skate
func sessionExistsSkate(sessionID string) bool {
	key := fmt.Sprintf("session@vinw-%s", sessionID)
	cmd := exec.Command("skate", "get", key)
	return cmd.Run() == nil
}

// deleteSessionSkate deletes a session from Skate
func deleteSessionSkate(sessionID string) error {
	key := fmt.Sprintf("session@vinw-%s", sessionID)
	cmd := exec.Command("skate", "delete", key)
	return cmd.Run()
}

// listSessionsSkate lists all vinw sessions in Skate
func listSessionsSkate() ([]string, error) {
	// List all databases
	cmd := exec.Command("skate", "list-dbs")
	output, err := cmd.Output()
	if err != nil {
		return nil, err
	}

	lines := strings.Split(string(output), "\n")
	var sessions []string

	for _, line := range lines {
		line = strings.TrimSpace(line)
		// Look for databases like "@vinw-{sessionID}"
		if strings.HasPrefix(line, "@vinw-") {
			// Extract session ID
			sessionID := strings.TrimPrefix(line, "@vinw-")
			if sessionID != "" {
				sessions = append(sessions, sessionID)
			}
		}
	}

	return sessions, nil
}

