	case tea.KeyMsg:
		switch m.step {
		case stepSelectAccount:
			switch msg.String() {
			case "up", "k":
				if m.selected > 0 {
					m.selected--
				}
			case "down", "j":
				if m.selected < len(m.accounts)-1 {
					m.selected++
				}
			case "enter":
				m.account = m.accounts[m.selected]
				m.step = stepConfirmCreate
			case "q", "esc":
				m.step = stepDeclined
				return m, tea.Quit
			}

		case stepConfirmCreate:
			switch msg.String() {
			case "y", "Y":
				m.step = stepEnterName
				return m, m.repoName.Focus()
			case "n", "N", "q", "esc":
				m.step = stepDeclined
				return m, tea.Quit
			}

		case stepEnterName:
			switch msg.String() {
			case "enter":
				if m.repoName.Value() != "" {
					m.step = stepEnterDescription
					m.repoName.Blur()
					return m, m.description.Focus()
				}
			case "esc":
				m.step = stepConfirmCreate
			default:
				m.repoName, cmd = m.repoName.Update(msg)
				return m, cmd
			}

		case stepEnterDescription:
			switch msg.String() {
