				m.viewport.SetContent(m.lastContent)
			} else if m.tree != nil {
				content := m.tree.String()
				m.viewport.SetContent(content)
				m.lastContent = content
			}
			m.ready = true
		} else {
			m.viewport.Width = msg.Width
			m.viewport.Height = msg.Height - verticalMargins
		}

	case tea.KeyMsg:
