func getGitDiffLines(filePath string) int {
	cmd := exec.Command("git", "diff", "--numstat", "HEAD", "--", filePath)
	output, err := cmd.Output()
	if err != nil {
		return 0
	}

	parts := strings.Fields(string(output))
	if len(parts) >= 1 {
		added, _ := strconv.Atoi(parts[0])
		return added
	}
	return 0
}

