const (
	stepSelectAccount setupStep = iota
	stepConfirmCreate
	stepEnterName
	stepEnterDescription
	stepSelectVisibility
	stepCreating
	stepDone
	stepDeclined
)

type githubSetupModel struct {
	step        setupStep
	accounts    []string
	selected    int
	account     string
	repoName    textinput.Model
	description textinput.Model
	isPublic    bool
	path        string
	err         error
	width       int
	height      int
}

